import React from "react";
import WidgetButton from "../../components/WidgetButton";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { deleteCourse } from "./courseSlice";

const UserList = () => {
  const dispatch = useDispatch();
  const Courses = useSelector((store) => store.Courses);

  console.log("====================================");
  console.log(Courses);
  console.log("====================================");

  const handleRemoveUser = (id) => {
    dispatch(deleteCourse({ id }));
  };

  const renderCard = () =>
  Courses.map((course) => (
      <div
        className="bg-green-200 p-5 flex items-center justify-between h-[8rem]"
        key={course.id}
      >
        <div>
          <h3 className="font-bold text-lg text-gray-700">Course Name :{courses.cname}</h3>
          <span className="font-normal text-gray-600">Course Description :{courses.desc}</span>
        </div>
        <div className="flex gap-2">
          <Link to={`/edit-user/${course.id}`}>
            <button>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth={2}
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z"
                />
              </svg>
            </button>
          </Link>
          <button onClick={() => handleRemoveUser(course.id)}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth={2}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
              />
            </svg>
          </button>
        </div>
      </div>
    ));

  return (
    <div>
      <Link to="/">
        <WidgetButton>Show Student</WidgetButton>
      </Link>
      <div className="grid gap-5 md:grid-cols-2">
        {Courses.length ? (
          renderCard()
        ) : (
          <p className="text-center col-span-2 text-gray-700 font-semibold">
            No Courses
          </p>
        )}
      </div>
    </div>
  );
};

export default UserList;
