import React from 'react'
import { useNavigate } from "react-router";
import { useDispatch } from "react-redux";
import WidgetTextfield from "../../components/WidgetTextfield";
import WidgetButton from "../../components/WidgetButton";

const AddCourse = () => {
    // const dispatch = useDispatch();
    // const navigate = useNavigate();

    const [values, setValues] = useState({
        fname: "",
        lname:"",
        email: "",
        contact_no:"",
        age: ""
    
      });



  return (
    <div className="mt-10 max-w-xl mx-auto">
      <WidgetTextfield
        label="First Name"
        value={values.fname}
        onChange={(e) => setValues({ ...values, fname: e.target.value })}
        inputProps={{ type: "text", placeholder: "Enter Student first name here" }}
      />
      <WidgetTextfield
        label="Last Name"
        value={values.lname}
        onChange={(e) => setValues({ ...values, lname: e.target.value })}
        inputProps={{ type: "text", placeholder: "Enter Student first name here" }}
      />
      {/* <br /> */}
      <WidgetTextfield
        label="Email"
        value={values.email}
        onChange={(e) => setValues({ ...values, email: e.target.value })}
        inputProps={{ type: "email", placeholder: "Enter Student email here" }}
      />
       <WidgetTextfield
        label="Contact Number"
        value={values.contact_no}
        onChange={(e) => setValues({ ...values, contact_no: e.target.value })}
        inputProps={{ type: "text", placeholder: "Enter Student contact_no here" }}
      />
      <WidgetTextfield
        label="age"
        value={values.age}
        onChange={(e) => setValues({ ...values, age: e.target.value })}
        inputProps={{ type: "email", placeholder: "Enter the age of student" }}
      />
      <WidgetButton >Submit</WidgetButton>
      </div>
  )
}

export default AddCourse
