import React, { useState } from "react";
import { useNavigate } from "react-router";
import { useDispatch } from "react-redux";
import WidgetTextfield from "../../components/WidgetTextfield";
import WidgetButton from "../../components/WidgetButton";
import { addUser } from "./userSlice";
import { v4 as uuidv4 } from "uuid";

const AddUser = () => {
  const dispatch = useDispatch();

  const navigate = useNavigate();

  const [values, setValues] = useState({
    name: "",
    email: "",
    age: ""
  });

  const handleAddUser = () => {
    setValues({ name: "", email: "" ,age:""});

    console.log("====================================");
    console.log(values);
    console.log("====================================");

    dispatch(
      addUser({
        id: uuidv4(),
        name: values.name,
        email: values.email,
        age:values.age
      })
    );

    navigate("/");
  };

  return (
    <div className="mt-10 max-w-xl mx-auto">
      <WidgetTextfield
        label="Name"
        value={values.name}
        onChange={(e) => setValues({ ...values, name: e.target.value })}
        inputProps={{ type: "text", placeholder: "Enter Student name here" }}
      />
      {/* <br /> */}
      <WidgetTextfield
        label="Email"
        value={values.email}
        onChange={(e) => setValues({ ...values, email: e.target.value })}
        inputProps={{ type: "email", placeholder: "Enter Student email here" }}
      />
      <WidgetTextfield
        label="age"
        value={values.age}
        onChange={(e) => setValues({ ...values, age: e.target.value })}
        inputProps={{ type: "email", placeholder: "Enter the age of student" }}
      />
      <WidgetButton onClick={handleAddUser}>Submit</WidgetButton>
    </div>
  );
};

export default AddUser;
