import React, { useState } from "react";
import { useNavigate, useParams } from "react-router";
import WidgetTextfield from "../../components/WidgetTextfield";
import WidgetButton from "../../components/WidgetButton";
import { useDispatch, useSelector } from "react-redux";
import { updateUser } from "./userSlice";

const UpdateUser = () => {
  const params = useParams();
  const dispatch = useDispatch();
  const users = useSelector((store) => store.users);
  const existingUser = users.filter((user) => user.id === params.id);
  const { name, email, age } = existingUser[0];
  console.log("====================================");
  console.log(existingUser);
  console.log("====================================");
  const [values, setValues] = useState({
    name,
    email,
    age,
  });

  const navigate = useNavigate();

  const handleUpdateUser = () => {
    setValues({ name: "", email: "" ,age:""});

    console.log("====================================");
    console.log(values);
    console.log("====================================");

    dispatch(
      updateUser({ id: params.id, name: values.name, email: values.email, age: values.age })
    );

    navigate("/");
  };

  return (
    <div className="mt-10 max-w-xl mx-auto">
      <WidgetTextfield
        label="Name"
        value={values.name}
        onChange={(e) => setValues({ ...values, name: e.target.value })}
        inputProps={{ type: "text", placeholder: "Enter Student name here" }}
      />
      <br />
      <WidgetTextfield
        label="Email"
        value={values.email}
        onChange={(e) => setValues({ ...values, email: e.target.value })}
        inputProps={{ type: "email", placeholder: "Enter Student email here" }}
      />
       <br />
      <WidgetTextfield
        label="age"
        value={values.age}
        onChange={(e) => setValues({ ...values, age: e.target.value })}
        inputProps={{ type: "text", placeholder: "How old are you" }}
      />
      <WidgetButton onClick={handleUpdateUser}>Update</WidgetButton>
    </div>
  );
};

export default UpdateUser;
