import { createSlice } from "@reduxjs/toolkit";

const initialState = [
  { id: "1", name: "Younus", email: "Younus@gmail.com", age:"20" },
  { id: "2", name: "Zaid", email: "Zaid@gmail.com", age:"26"  },
  { id: "3", name: "Alex", email: "alex@gmail.com", age:"16"  },
  { id: "4", name: "Ronaldo", email: "Ronaldo@gmail.com", age:"25"  },
];

const userSlice = createSlice({
  name: "users",
  initialState,
  reducers: {
    addUser: (state, action) => {
      console.log("====================================");
      console.log(action);
      console.log("====================================");
      state.push(action.payload);
    },
    updateUser: (state, action) => {
      const { id, name, email ,age } = action.payload;
      const existingUser = state.find((user) => user.id === id);
      if (existingUser) {
        existingUser.name = name;
        existingUser.email = email;
        existingUser.age = age;
      }
    },
    deleteUser: (state, action) => {
      const { id } = action.payload;
      const existingUser = state.find((user) => user.id === id);
      if (existingUser) {
        return state.filter((user) => user.id !== id);
      }
    },
  },
});

export const { addUser, updateUser, deleteUser } = userSlice.actions;
export default userSlice.reducer;
