  //   // dispatch(
  //   //   updateUser({ id: params.id, ...values})
  //   // );

  //   navigate("/");
  // };

  return (
    <div className="mt-10 max-w-xl mx-auto">
      <WidgetTextfield
        label="First Name"
        // value={values.fname}
        // onChange={(e) => setValues({ ...values, fname: e.target.value })}
        inputProps={{ type: "text", placeholder: "Enter Student fname here" }}
      />
